Bash(DATABASE_USER=simplia DATABASE_PASSWORD=1234 DATABASE_NAME=simplia_paas_test node -e "
const { Pool } = require(''pg'');
const pool = new Pool({
  user: ''simplia'',
  host: ''localhost'', 
  database: ''simplia_paas_test'',
  password: ''1234'',
  port: 5432,
});

async function checkTenant5() {
  try {
    // Get tenant 5 info
    const tenantResult = await pool.query(''SELECT id, name, subdomain FROM tenants WHERE id = 5'');
    console.log(''Tenant 5:'', tenantResult.rows[0]);

    // Check what schemas exist
    const schemaResult = await pool.query(""SELECT schema_name FROM information_schema.schemata WHERE schema_name LIKE ''tenant_%'' ORDER BY schema_name"");
    console.log(''Available schemas:'', schemaResult.rows.map(r => r.schema_name));

    pool.end();
  } catch (err) {
    console.error(''Error:'', err);
    pool.end();
  }
}
checkTenant5();
