-- DENTISTRY SPECIALTY — pt-BR
-- ============================================================================

INSERT INTO public.marketplace_items (type, title, description, content, specialty, locale, thumbnail_url) VALUES
('template', 'Avaliação Odontológica', 'Template para avaliação odontológica inicial com exame intraoral, diagnóstico e plano de tratamento.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Avaliação Odontológica</mark></strong></p><p></p><p><strong>Paciente:</strong> $patient.fullName$</p><p><strong>Dentista:</strong> $me.fullName$</p><p><strong>Data:</strong> $session.created_at$</p><p></p><p><strong>Queixa Principal</strong></p><p>[Motivo da consulta e sintomas relatados pelo paciente.]</p><p></p><p><strong>Exame Intraoral</strong></p><p>- Tecidos moles: [Mucosa, gengiva, língua, palato]</p><p>- Periodonto: [Sondagem, mobilidade, sangramento]</p><p>- Dentição: [Cáries, restaurações, ausências]</p><p>- Oclusão: [Classificação, interferências]</p><p>- ATM: [Abertura, ruídos, dor]</p><p></p><p><strong>Exames Complementares</strong></p><p>[Radiografias solicitadas/avaliadas e achados.]</p><p></p><p><strong>Diagnóstico</strong></p><p>[Diagnósticos odontológicos identificados por dente/região.]</p><p></p><p><strong>Plano de Tratamento</strong></p><p>1. [Procedimento — dente/região — prioridade]</p><p>2. [Procedimento — dente/região — prioridade]</p><p>3. [Procedimento — dente/região — prioridade]</p><p></p><p><strong>Orientações ao Paciente</strong></p><p>[Higiene oral, cuidados pós-procedimento, alimentação.]</p><p></p><p><strong>Próxima Sessão</strong></p><p>[Data e procedimento planejado.]</p>', 'dentistry', 'pt-BR', null),

('template', 'Nota de Procedimento Odontológico', 'Template para registro de procedimentos odontológicos realizados durante a sessão.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Nota de Procedimento</mark></strong></p><p></p><p><strong>Paciente:</strong> $patient.fullName$</p><p><strong>Dentista:</strong> $me.fullName$</p><p><strong>Data:</strong> $session.created_at$</p><p></p><p><strong>Procedimento Realizado</strong></p><p>[Descreva o procedimento, dente(s) tratado(s), técnica utilizada e materiais empregados.]</p><p></p><p><strong>Anestesia</strong></p><p>[Tipo de anestésico, técnica, quantidade e região anestesiada.]</p><p></p><p><strong>Intercorrências</strong></p><p>[Descreva qualquer intercorrência durante o procedimento ou indique "Sem intercorrências".]</p><p></p><p><strong>Orientações Pós-Operatórias</strong></p><p>- Medicação: [Prescrições e posologia]</p><p>- Cuidados: [Orientações específicas de pós-operatório]</p><p>- Alimentação: [Restrições alimentares temporárias]</p><p></p><p><strong>Retorno</strong></p><p>[Data de retorno e motivo — reavaliação, continuidade do tratamento.]</p>', 'dentistry', 'pt-BR', null);

