-- DENTISTRY SPECIALTY — en-US
-- ============================================================================

INSERT INTO public.marketplace_items (type, title, description, content, specialty, locale, thumbnail_url) VALUES
('template', 'Dental Assessment', 'Template for initial dental assessment with intraoral exam, diagnosis and treatment plan.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Dental Assessment</mark></strong></p><p></p><p><strong>Patient:</strong> $patient.fullName$</p><p><strong>Dentist:</strong> $me.fullName$</p><p><strong>Date:</strong> $session.created_at$</p><p></p><p><strong>Chief Complaint</strong></p><p>[Reason for visit and symptoms reported by the patient.]</p><p></p><p><strong>Intraoral Examination</strong></p><p>- Soft tissues: [Mucosa, gingiva, tongue, palate]</p><p>- Periodontal: [Probing, mobility, bleeding]</p><p>- Dentition: [Caries, restorations, missing teeth]</p><p>- Occlusion: [Classification, interferences]</p><p>- TMJ: [Opening, sounds, pain]</p><p></p><p><strong>Diagnostic Imaging</strong></p><p>[Radiographs ordered/evaluated and findings.]</p><p></p><p><strong>Diagnosis</strong></p><p>[Dental diagnoses identified by tooth/region.]</p><p></p><p><strong>Treatment Plan</strong></p><p>1. [Procedure — tooth/region — priority]</p><p>2. [Procedure — tooth/region — priority]</p><p>3. [Procedure — tooth/region — priority]</p><p></p><p><strong>Patient Instructions</strong></p><p>[Oral hygiene, post-procedure care, dietary guidance.]</p><p></p><p><strong>Next Appointment</strong></p><p>[Date and planned procedure.]</p>', 'dentistry', 'en-US', null),

('template', 'Dental Procedure Note', 'Template for recording dental procedures performed during the session.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Procedure Note</mark></strong></p><p></p><p><strong>Patient:</strong> $patient.fullName$</p><p><strong>Dentist:</strong> $me.fullName$</p><p><strong>Date:</strong> $session.created_at$</p><p></p><p><strong>Procedure Performed</strong></p><p>[Describe the procedure, tooth/teeth treated, technique used and materials employed.]</p><p></p><p><strong>Anesthesia</strong></p><p>[Anesthetic type, technique, dosage and region anesthetized.]</p><p></p><p><strong>Complications</strong></p><p>[Describe any complications during the procedure or note "No complications".]</p><p></p><p><strong>Post-Operative Instructions</strong></p><p>- Medication: [Prescriptions and dosage]</p><p>- Care: [Specific post-operative instructions]</p><p>- Diet: [Temporary dietary restrictions]</p><p></p><p><strong>Follow-Up</strong></p><p>[Return date and reason — re-evaluation, treatment continuation.]</p>', 'dentistry', 'en-US', null);

