INSERT INTO public.marketplace_items (type, title, description, content, specialty, locale, thumbnail_url) VALUES
('template', 'Avaliação Nutricional', 'Template para avaliação nutricional com anamnese alimentar, antropometria e plano alimentar.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Avaliação Nutricional</mark></strong></p><p></p><p><strong>Paciente:</strong> $patient.fullName$</p><p><strong>Nutricionista:</strong> $me.fullName$</p><p><strong>Data:</strong> $session.created_at$</p><p></p><p><strong>Motivo da Consulta</strong></p><p>[Objetivo do paciente: emagrecimento, ganho de massa, reeducação alimentar, patologia específica.]</p><p></p><p><strong>Anamnese Alimentar</strong></p><p>- Recordatório 24h: [Descreva as refeições do dia anterior]</p><p>- Preferências: [Alimentos preferidos e rejeitados]</p><p>- Restrições: [Alergias, intolerâncias, restrições religiosas/culturais]</p><p>- Suplementos: [Suplementos em uso]</p><p>- Hidratação: [Ingestão diária de água]</p><p></p><p><strong>Antropometria</strong></p><p>- Peso: [___] kg</p><p>- Altura: [___] m</p><p>- IMC: [___] kg/m²</p><p>- Circunferência abdominal: [___] cm</p><p>- % Gordura corporal: [___]%</p><p></p><p><strong>Exames Laboratoriais</strong></p><p>[Resultados de exames relevantes: glicemia, colesterol, triglicerídeos, etc.]</p><p></p><p><strong>Diagnóstico Nutricional</strong></p><p>[Estado nutricional e classificação.]</p><p></p><p><strong>Plano Alimentar</strong></p><p>[Objetivo calórico, distribuição de macronutrientes e orientações gerais.]</p><p></p><p><strong>Orientações</strong></p><p>[Recomendações de estilo de vida, atividade física e hábitos alimentares.]</p><p></p><p><strong>Retorno</strong></p><p>[Data e objetivos para a próxima consulta.]</p>', 'nutrition', 'pt-BR', null),

('template', 'Evolução Nutricional', 'Template para acompanhamento e evolução de pacientes em tratamento nutricional.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Evolução Nutricional</mark></strong></p><p></p><p><strong>Paciente:</strong> $patient.fullName$</p><p><strong>Nutricionista:</strong> $me.fullName$</p><p><strong>Data:</strong> $session.created_at$</p><p></p><p><strong>Adesão ao Plano</strong></p><p>[Avalie a adesão do paciente ao plano alimentar anterior, dificuldades encontradas e ajustes realizados.]</p><p></p><p><strong>Evolução Antropométrica</strong></p><p>- Peso atual: [___] kg (anterior: [___] kg)</p><p>- Variação: [+/-___] kg</p><p>- IMC atual: [___]</p><p>- Circunferência abdominal: [___] cm</p><p></p><p><strong>Sintomas e Queixas</strong></p><p>[Sintomas gastrointestinais, energia, sono, humor e outros relatos.]</p><p></p><p><strong>Ajustes no Plano</strong></p><p>[Mudanças realizadas no plano alimentar com justificativa.]</p><p></p><p><strong>Metas para Próximo Período</strong></p><p>[Objetivos específicos e mensuráveis até a próxima consulta.]</p><p></p><p><strong>Retorno</strong></p><p>[Data da próxima consulta.]</p>', 'nutrition', 'pt-BR', null);

-- ============================================================================
-- NUTRITION SPECIALTY — en-US
-- ============================================================================

INSERT INTO public.marketplace_items (type, title, description, content, specialty, locale, thumbnail_url) VALUES
('template', 'Nutritional Assessment', 'Template for nutritional assessment with dietary history, anthropometry and meal plan.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Nutritional Assessment</mark></strong></p><p></p><p><strong>Patient:</strong> $patient.fullName$</p><p><strong>Nutritionist:</strong> $me.fullName$</p><p><strong>Date:</strong> $session.created_at$</p><p></p><p><strong>Reason for Visit</strong></p><p>[Patient goals: weight loss, muscle gain, dietary reeducation, specific condition.]</p><p></p><p><strong>Dietary History</strong></p><p>- 24h recall: [Describe meals from the previous day]</p><p>- Preferences: [Preferred and disliked foods]</p><p>- Restrictions: [Allergies, intolerances, religious/cultural restrictions]</p><p>- Supplements: [Current supplements]</p><p>- Hydration: [Daily water intake]</p><p></p><p><strong>Anthropometry</strong></p><p>- Weight: [___] kg</p><p>- Height: [___] m</p><p>- BMI: [___] kg/m²</p><p>- Waist circumference: [___] cm</p><p>- Body fat %: [___]%</p><p></p><p><strong>Lab Results</strong></p><p>[Relevant lab results: glucose, cholesterol, triglycerides, etc.]</p><p></p><p><strong>Nutritional Diagnosis</strong></p><p>[Nutritional status and classification.]</p><p></p><p><strong>Meal Plan</strong></p><p>[Caloric target, macronutrient distribution and general guidelines.]</p><p></p><p><strong>Recommendations</strong></p><p>[Lifestyle, physical activity and dietary habit recommendations.]</p><p></p><p><strong>Follow-Up</strong></p><p>[Date and goals for next appointment.]</p>', 'nutrition', 'en-US', null),

('template', 'Nutritional Progress Note', 'Template for tracking nutritional treatment progress and follow-up.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Nutritional Progress Note</mark></strong></p><p></p><p><strong>Patient:</strong> $patient.fullName$</p><p><strong>Nutritionist:</strong> $me.fullName$</p><p><strong>Date:</strong> $session.created_at$</p><p></p><p><strong>Plan Adherence</strong></p><p>[Evaluate patient adherence to previous meal plan, difficulties encountered and adjustments made.]</p><p></p><p><strong>Anthropometric Progress</strong></p><p>- Current weight: [___] kg (previous: [___] kg)</p><p>- Change: [+/-___] kg</p><p>- Current BMI: [___]</p><p>- Waist circumference: [___] cm</p><p></p><p><strong>Symptoms and Concerns</strong></p><p>[GI symptoms, energy, sleep, mood and other reports.]</p><p></p><p><strong>Plan Adjustments</strong></p><p>[Changes made to the meal plan with rationale.]</p><p></p><p><strong>Goals for Next Period</strong></p><p>[Specific, measurable objectives until next appointment.]</p><p></p><p><strong>Follow-Up</strong></p><p>[Next appointment date.]</p>', 'nutrition', 'en-US', null);

