INSERT INTO public.marketplace_items (type, title, description, content, specialty, locale, thumbnail_url) VALUES
('template', 'Resumo da Consulta', 'Template para resumos de consultas médicas com seções de exame, achados, tratamentos e recomendações.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Resumo da Consulta</mark></strong></p><p></p><p><strong>Paciente:</strong> $patient.fullName$</p><p><strong>Profissional:</strong> $me.fullName$</p><p><strong>Data:</strong> $session.created_at$</p><p></p><p><strong>Motivo da Consulta</strong></p><p>[Descreva o motivo principal da consulta e queixas apresentadas pelo paciente.]</p><p></p><p><strong>Exame Clínico</strong></p><p>[Descreva os achados do exame físico, incluindo sinais vitais e observações relevantes.]</p><p></p><p><strong>Diagnóstico</strong></p><p>[Liste os diagnósticos identificados durante a consulta.]</p><p></p><p><strong>Conduta</strong></p><p>[Descreva o plano de tratamento, medicações prescritas e orientações dadas ao paciente.]</p><p></p><p><strong>Retorno</strong></p><p>- Próxima consulta: [Data ou período recomendado]</p><p>- Exames solicitados: [Liste exames pendentes]</p><p></p><p>Atenciosamente,</p><p>$me.fullName$</p>', 'general', 'pt-BR', null),

('template', 'Nota Clínica Geral', 'Template completo para notas clínicas com anamnese, exame, diagnóstico e plano terapêutico.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Nota Clínica</mark></strong></p><p></p><p>Dr(a). $me.fullName$ atendeu $patient.fullName$ em $session.created_at$.</p><p></p><p><strong>Anamnese</strong></p><p>[Queixa principal, história da doença atual, antecedentes pessoais e familiares.]</p><p></p><p><strong>Exame Físico</strong></p><p>- Estado geral: [Descrição]</p><p>- Sinais vitais: PA: [___] FC: [___] FR: [___] Temp: [___]</p><p>- Exame segmentar: [Achados por sistemas]</p><p></p><p><strong>Hipóteses Diagnósticas</strong></p><p>[Liste as hipóteses diagnósticas consideradas.]</p><p></p><p><strong>Plano Terapêutico</strong></p><p>[Medicações, procedimentos, encaminhamentos e orientações.]</p><p></p><p><strong>Observações</strong></p><p>[Informações adicionais relevantes.]</p>', 'general', 'pt-BR', null);

