INSERT INTO public.marketplace_items (type, title, description, content, specialty, locale, thumbnail_url) VALUES
('template', 'Consultation Summary', 'Template for medical consultation summaries with examination, findings, treatments and recommendations.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Consultation Summary</mark></strong></p><p></p><p><strong>Patient:</strong> $patient.fullName$</p><p><strong>Provider:</strong> $me.fullName$</p><p><strong>Date:</strong> $session.created_at$</p><p></p><p><strong>Reason for Visit</strong></p><p>[Describe the main reason for the consultation and patient complaints.]</p><p></p><p><strong>Clinical Examination</strong></p><p>[Describe physical exam findings, including vital signs and relevant observations.]</p><p></p><p><strong>Diagnosis</strong></p><p>[List diagnoses identified during the consultation.]</p><p></p><p><strong>Treatment Plan</strong></p><p>[Describe the treatment plan, prescribed medications, and patient instructions.]</p><p></p><p><strong>Follow-Up</strong></p><p>- Next appointment: [Recommended date or timeframe]</p><p>- Ordered tests: [List pending tests]</p><p></p><p>Sincerely,</p><p>$me.fullName$</p>', 'general', 'en-US', null),

('template', 'General Clinical Note', 'Complete clinical note template with history, examination, diagnosis and treatment plan.', '<p><strong><mark data-color="var(--tt-color-highlight-purple)" style="background-color: var(--tt-color-highlight-purple); color: inherit;">Clinical Note</mark></strong></p><p></p><p>Dr. $me.fullName$ saw $patient.fullName$ on $session.created_at$.</p><p></p><p><strong>History</strong></p><p>[Chief complaint, history of present illness, past medical and family history.]</p><p></p><p><strong>Physical Examination</strong></p><p>- General: [Description]</p><p>- Vitals: BP: [___] HR: [___] RR: [___] Temp: [___]</p><p>- Systems review: [Findings by system]</p><p></p><p><strong>Assessment</strong></p><p>[List diagnostic considerations.]</p><p></p><p><strong>Plan</strong></p><p>[Medications, procedures, referrals and patient education.]</p><p></p><p><strong>Notes</strong></p><p>[Additional relevant information.]</p>', 'general', 'en-US', null);

